<?phpclass DBDS {	private $driver_ds;	public function __construct($driver_ds, $hostname, $username, $password, $database) {				 if ($driver_ds!='mysql') {$driver_ds='mysql';}		 		$file = DIR_DATABASE . $driver_ds . '.php';				if (file_exists($file)) {			require_once($file);			if (class_exists('mysql')) {				$class = $driver_ds;			} else {						$class = 'DB' . $driver_ds;						}			$this->driver_ds = new $class($hostname, $username, $password, $database);		} else {			exit('Error: Could not load database driver type ' . $driver_ds . '!');		}					}	public function query($sql) {		return $this->driver_ds->query($sql);	}	public function escape($value) {		return $this->driver_ds->escape($value);	}	public function countAffected() {		return $this->driver_ds->countAffected();	}	public function getLastId() {		return $this->driver_ds->getLastId();	}	}?>